mapper.select:
SELECT pnr.id as id, pnr.namespace_id AS namespaceId, pnr.plugin_id AS pluginId, pnr.config AS config, pnr.sort AS sort, pnr.enabled AS enabled, pnr.date_created AS dateCreated, pnr.date_updated AS dateUpdated, p.role AS role, p.NAME AS NAME, p.plugin_jar AS pluginJar FROM ( SELECT
FROM plugin_ns_rel
AND enabled = #{enabled, jdbcType=TINYINT}
AND namespace_id = #{namespaceId, jdbcType=TINYINT}
) pnr LEFT JOIN plugin p ON p.id = pnr.plugin_id
AND p.name LIKE #{nameLike, jdbcType=VARCHAR}
ORDER BY sort, pnr.id ASC

